import {createReducer} from "@reduxjs/toolkit";
import axios from "axios";
let products = {}
const res= axios.get ('http://localhost:1337/api/products')
    .then (res=>console.log(res.data.data))

const defaultState={
     products:[res]
}

export default createReducer(defaultState,{
})
