import classes from './RecomendProduct.module.sass'
import {useState} from "react";

const RecomendProduct = (props) => {
const [button, setButton]=useState("Электроника")


    return (
    <div className={classes.RecomendProduct}>
        <h2>{props.text}</h2>
        <div className={classes.buttonBlock}>
            <button>Запчасти</button>
            <button>Моторы</button>
            <button>Шины</button>
            <button className={button==="Электроника" && classes.active}>Электроника</button>
            <button>Инструменты</button>
            <button>Аксессуары</button>
        </div>
    </div>
    )

}

export default RecomendProduct
