import classes from './Slider.module.sass'
import { Pagination, Navigation } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import bannerPng from "../../images/png/banner.png"
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
const photos = [
    'https://snegik.ru/upload/iblock/b91/b91c145a16585a7f061efbea5b791b8c.jpeg',
    'https://5koleso.ru/wp-content/cache/thumb/08/d850a3ce912e808_1200x580.jpg',
    'https://watermark.lovepik.com/photo/45014/5112.jpg_wh1200.jpg',
    'https://res.klook.com/image/upload/c_fill,w_750,h_560/q_80/w_80,x_15,y_15,g_south_west,l_Klook_water_br_trans_yhcmh3/activities/axpngyztmkuk8sxu0psq.jpg'
]
const Slider = () => {

    return (
        <>
            <Swiper
                slidesPerView={1}
                spaceBetween={30}
                loop={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation]}
                className={classes.swiper}
            >
                {photos.map((photo, index)=>{
                    <SwiperSlide><img className={classes.img} key={index} src={photo} alt="hydra"/></SwiperSlide>
                })}
            </Swiper>
        </>

    )

}

export default Slider
