import classes from './RecomendProduct.module.sass'
import {useState} from "react";

const RecomendProduct = (props) => {
const [button, setButton]=useState("Электроника")
    const handleClickButton =(name)=>{
        switch (name){
            case 'name': setButton(name)
                console.log(button)

            default:
                break
        }
    }

    return (
    <div className={classes.RecomendProduct}>
        <h2>{props.text}</h2>
        <div className={classes.buttonBlock}>
            <button  onClick={()=>handleClickButton("Запчасти")} className={button==="Запчасти" && classes.active}>Запчасти</button>
            <button onClick={()=>handleClickButton("Шины")}>Шины</button>
            <button onClick={()=>handleClickButton("Моторы")}>Моторы</button>
            <button onClick={()=>handleClickButton("Электроника")} className={button==="Электроника" && classes.active}>Электроника</button>
            <button onClick={()=>handleClickButton("Инструменты")}>Инструменты</button>
            <button onClick={()=>handleClickButton("Аксессуары")}>Аксессуары</button>
        </div>
    </div>
    )

}

export default RecomendProduct
