import classes from './ShopsBlock.module.sass'
import {objectValue} from "../../assets/test/Tests";
import BlueButton from "../UI/BlueButton/BlueButton";
import Shop from "../Shop/Shop";
import {useEffect, useState} from "react";

const ShopsBlock = ({productId, markets}) => {
    const [arr, setArr] = useState([])
useEffect(()=>{
    markets.forEach(res=>setArr(res.attributes.products))
    arr.forEach(elem=>{
        console.log(elem)
    })
}, [])
    console.log(arr)

    return (
        <div className={classes.shopsBlock}>
            <div className={classes.shopsHeader}>
                <a href="#">О товаре</a>
                <a href="#">Характеристики</a>
                <a href="#">Отзывы</a>
                <a className={classes.active} href="#">Самовывоз</a>
                <a href="#">Доставка</a>
                <a href="#">Сервис</a>
                <a href="#">Гарантия</a>
            </div>
            <div className={classes.shopsInfo}>
                <form>
                    <h2>Магазин</h2> <input type='text'/>
                </form>
                <div className={classes.shops}>
                    <div className={classes.caption}>
                        <h2>Адрес</h2>
                        <h2>Режим работы</h2>
                        <h2>Доступно</h2>
                        <h2>Количество</h2>
                    </div>
                    {markets.map(res=><Shop key={res.attributes.uid} name={res.attributes.marketName} workTime={res.attributes.workTime}/>)}

                < /div>
            </div>
        </div>
    )

}

export default ShopsBlock
