import classes from './Search.module.sass'
import BlueButton from "../UI/BlueButton/BlueButton";
import {useState} from "react";

const Search = () => {
    const [active, setActive]=useState('mark')
    const handleClickButton =()=>{
        setActive('name')
    }


    return (
        <div className={classes.search}>
    <div className={classes.searchTypeBlock}>
        <button onClick={handleClickButton} className={active === 'number' && classes.active} >Поиск по номер</button>
        <button onClick={handleClickButton} className={active === 'mark' && classes.active} >Поиск по марке</button>
        <button onClick={handleClickButton} className={active === 'name' && classes.active}>Поиск по названию товара</button>
    </div>
        <form className={classes.searchForm}>
            <input placeholder="Поиск" type="text"/>
            <BlueButton>ИСКАТЬ</BlueButton>
        </form>
        </div>
    )

}

export default Search
