import classes from './Search.module.sass'
import BlueButton from "../UI/BlueButton/BlueButton";
import {useState} from "react";

const Search = () => {
    const [active, setActive]=useState('')
    const activeNumber =()=>{
      setActive('number')
    }
    const activeMark =()=>{

            setActive('mark')

    }
    const activeName =()=>{

            setActive('name')


    }
    return (
        <div className={classes.search}>
    <div className={classes.searchTypeBlock}>
        <button onClick={activeNumber} className={active === 'number'? classes.active: ''} >Поиск по номер</button>
        <button onClick={activeMark} className={active === 'mark'? classes.active: ''} >Поиск по марке</button>
        <button onClick={activeName} className={active === 'name'? classes.active: ''}>Поиск по названию товара</button>
    </div>
        <form className={classes.searchForm}>
            <input placeholder="Поиск" type="text"/>
            <BlueButton>ИСКАТЬ</BlueButton>
        </form>
        </div>
    )

}

export default Search
