import {createReducer} from "@reduxjs/toolkit";
import axios from "axios";



const defaultState={
       state: ()=>{
           axios.get ('http://localhost:1337/api/products')
               .then (res=> res.data)
       }
}
console.log(defaultState)
export default createReducer(defaultState,{
})
